function updateSetting(e){var t="api.setting.php",a=$("#value-"+e).val();$.ajax({url:t,cache:!1,dataType:"json",type:"POST",data:{calling:"setting",action:"update_setting",id:e,value:a},error:function(){console.log("Request Error")}}).done(function(e){console.log("Return: "+e.message),location.reload()}).error()}